package lamen.cipher;import android.content.Context;import android.content.Intent;import android.util.AttributeSet;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.RelativeLayout;import android.widget.TextView;import lamen.cipher.generate.GenerateGestureActivity;import lamen.cipher.generate.GenerateLinkActivity;import lamen.cipher.generate.GenerateScanActivity;import lamen.cipher.generate.GenerateTextActivity;public class MainElement extends RelativeLayout {    ImageView bg_image;    Context context;    Integer ways_id;    Integer text_id;    public MainElement(Context con) {        super(con);        context = con;        LayoutInflater.from(context).inflate(R.layout.main_element, this);        bg_image = (ImageView)findViewById(R.id.element_bg_imageview);        ViewGroup.LayoutParams params = new RelativeLayout.LayoutParams(                ((MainActivity)context).getWindowManager().getDefaultDisplay().getWidth()*9/10,                ((MainActivity)context).getWindowManager().getDefaultDisplay().getHeight() /4);        setLayoutParams(params);        setPadding(0,0,0,15);        setOnClickListener(new OnClickListener() {            @Override            public void onClick(View v) {                Intent intent = null;                switch (ways_id){                    case R.mipmap.cipher_doublepassword:                        ((MainActivity) context).ShowDoublePWDialog();                        break;                    case R.mipmap.cipher_text:                        intent = new Intent();                        intent.setClass(context, GenerateTextActivity.class);                        break;                    case R.mipmap.cipher_gesture:                        intent = new Intent();                        intent.setClass(context, GenerateGestureActivity.class);                        break;                    case R.mipmap.cipher_link:                        intent = new Intent();                        intent.setClass(context, GenerateLinkActivity.class);                        break;                    case R.mipmap.cipher_scan:                        intent = new Intent();                        intent.setClass(context, GenerateScanActivity.class);                        break;                }                if (intent != null) {                    context.startActivity(intent);                    ((MainActivity) context).overridePendingTransition(R.anim.activity_exit_anim, 0);                }            }        });    }    public MainElement(Context context, AttributeSet attrs) {        super(context, attrs);    }    public MainElement(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);    }    public void setTypeImageFromRes(int res){        ImageView type_image = (ImageView)findViewById(R.id.element_type_imageview);        type_image.setImageResource(res);    }    public void setTypeText(String text){        TextView type_text = (TextView)findViewById(R.id.element_type_textview);        type_text.setText(text);    }}